var backToHome = function() {
  var stack = OF.pageStackController.activeStack;
  var rootPage = stack.pages[0];
  // friends profile page has same url as my profile page, use !stack.activePage.params['user_id'] to confirm it's my profile page
  if (stack.activePage.url.match(rootPage.url) && !stack.activePage.params['user_id']) {
    OF.refresh()
  } else {
    OF.goBack({root:true});
  }  
}
// Menu handling functions
var menuControl = OF.menuControl;
menuControl.enableMenuItems('home', 'settings', 'exit');
menuControl.initialize();
menuControl.setup('home', backToHome);
menuControl.setup('settings', function() {
  OF.action('openSettings');
});
menuControl.setup('exit', function() {
  OF.action('dismiss');
});

// Go home on touching the logo area
$('#home_button').touch(backToHome);

$('#header .title').html(OF.page.title);
